import React, { useState, useEffect, useRef } from 'react';
import * as d3 from 'd3';

const StockGraph = () => {
    const [stock, setStock] = useState('AAPL'); // Default stock
    const [start, setStart] = useState('2000-01-01'); // Default start date
    const [end, setEnd] = useState(new Date().toISOString().split('T')[0]); // Default to today
    const [purchaseAmount, setPurchaseAmount] = useState(1000); // Default purchase amount
    const [stockData, setStockData] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    const svgRef = useRef(); // Ref for the SVG element

    const fetchStockData = async () => {
        setLoading(true);
        setError(null);
        try {
            var url = `http://localhost:5000/api/stocks?stock=${stock}&start=${start}&end=${end}`;
            console.log('url',url)
            const response = await fetch(url);
            if (!response.ok) throw new Error('Network response was not ok');
            const result = await response.json();
            // Assuming result has the necessary data for the graph
            // Process the result to extract data points
            const chartData = result.chart.result[0].indicators.quote[0];
            const dates = result.chart.result[0].timestamp.map((timestamp) => new Date(timestamp * 1000));
            const prices = chartData.close;

            // Combine dates and prices
            const formattedData = dates.map((date, index) => ({ date, price: prices[index] })).filter(d => d.price != null);
            setData(formattedData);
        } catch (err) {
            setError(err.message);
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        if (stockData.length) {
            drawGraph(); // Call the function to draw the graph
        }
    }, [stockData]);

    const drawGraph = () => {
        // Set dimensions and margins for the graph
        const margin = { top: 20, right: 30, bottom: 40, left: 40 };
        const width = 800 - margin.left - margin.right;
        const height = 400 - margin.top - margin.bottom;

        // Clear any previous SVG
        d3.select(svgRef.current).selectAll('*').remove();

        // Create an SVG element
        const svg = d3.select(svgRef.current)
            .attr('width', width + margin.left + margin.right)
            .attr('height', height + margin.top + margin.bottom)
            .append('g')
            .attr('transform', `translate(${margin.left},${margin.top})`);

        // Set the scales
        const x = d3.scaleTime()
            .domain(d3.extent(stockData, d => d.date))
            .range([0, width]);

        const y = d3.scaleLinear()
            .domain([0, d3.max(stockData, d => d.price)])
            .range([height, 0]);

        // Add the axes
        svg.append('g')
            .attr('class', 'x-axis')
            .attr('transform', `translate(0,${height})`)
            .call(d3.axisBottom(x).tickFormat(d3.timeFormat("%Y-%m-%d")));

        svg.append('g')
            .attr('class', 'y-axis')
            .call(d3.axisLeft(y));

        // Draw the line
        const line = d3.line()
            .x(d => x(d.date))
            .y(d => y(d.price));

        svg.append('path')
            .datum(data)
            .attr('fill', 'none')
            .attr('stroke', 'steelblue')
            .attr('stroke-width', 1.5)
            .attr('d', line);
    };

    // Function to calculate profit
    const calculateProfit = () => {
        if (stockData.length === 0) return;

        // Assuming stockData[0].price is the initial price and stockData[stockData.length - 1].price is the final price
        const initialPrice = stockData[0].price;
        const finalPrice = stockData[stockData.length - 1].price;

        // Calculate the profit/loss
        const sharesBought = investmentAmount / initialPrice;
        const finalValue = sharesBought * finalPrice;
        const profitAmount = finalValue - investmentAmount;

        setProfit(profitAmount);
    };



    const handleSubmit = (e) => {
        e.preventDefault();
        fetchStockData(); // Fetch data on form submission
    };

    return (
        <div>
            <h1>Stock Investment Graph</h1>
            <form onSubmit={handleSubmit}>
                <table>
                    <thead>
                        <tr>
                            <th>Stock:</th>
                            <th>Start:</th>
                            <th>End:</th>
                            <th colSpan='100%'>Purchase Amount:</th>
                        </tr>
                    </thead>
                    <tbody>
                        <tr>
                            <td>
                                <label>
                                    <input
                                        type="text"
                                        placeholder="Stock Symbol"
                                        value={stock}
                                        onChange={(e) => setStock(e.target.value)}
                                    />
                                </label>
                            </td>
                            <td>
                                <label>
                                    <input
                                        type="date"
                                        value={start}
                                        onChange={(e) => setStart(e.target.value)}
                                    />
                                </label>
                            </td>
                            <td>
                                <label>
                                    <input
                                        type="date"
                                        value={end}
                                        onChange={(e) => setEnd(e.target.value)}
                                    />
                                </label>
                            </td>
                            <td>
                                <label>
                                    <input
                                        type="number"
                                        value={purchaseAmount}
                                        onChange={(e) => setPurchaseAmount(e.target.value)}
                                        placeholder="Purchase Amount in USD"
                                    />
                                </label>
                            </td>
                            <td>
                                <button type="submit">Fetch Data</button>
                            </td>
                        </tr>
                    </tbody>
                </table>
            </form>
            {loading && <p>Loading...</p>}
            {error && <p>Error: {error}</p>}
            <svg ref={svgRef}></svg> {/* SVG for D3 to draw on */}
        </div>
    );
};

export default StockGraph;
