import React, { useState, useEffect, useRef } from 'react';
import * as d3 from 'd3';
import axios from 'axios';

import useDebounce from './../hooks/useDebounce';

const StockGraph = () => {
    const [stock, setStock] = useState('AAPL'); // this is actually the stock symbol not the name
    const debouncedStock = useDebounce(stock, 500); // this gives the user a chance to finish writing

    const [startDate, setStartDate] = useState('2024-10-15'); // Default start date
    const [endDate, setEndDate] =  useState('2024-10-16'); // Default start date
    const [purchaseAmount, setPurchaseAmount] = useState(1000); // Default purchase amount
    const [stockData, setStockData] = useState([]);
    const [profit, setProfit] = useState(null);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const [earliestDate, setEarliestDate] = useState('');

    const [formattedStockData, setFormattedStockData] = useState('');

    const svgRef = useRef(); // Ref for the SVG element

    useEffect(()=>{
        setFormattedStockData(JSON.stringify(stockData, null, 2)); // 2 spaces for pretty printing
    }
    ,[stockData])

    // useEffect(() => {
    //     const fetchEarliestDate = async () => {
    //         try {
    //             const response = await axios.get(`/api/earliest-date/${stock}`);
    //             setEarliestDate(response.data.earliestDate);
    //         } catch (error) {
    //             console.error('Error fetching earliest date:', error);
    //         }
    //     };

    //     if (stock) {
    //         fetchEarliestDate();
    //     }
    // }, [stock]);

    const fetchStockData = async () => {
        setLoading(true);
        setError(null);
        try {
            var url = `http://localhost:5000/api/stocks?stock=${stock}&startDate=${startDate}&endDate=${endDate}`;
            // console.log('url',url)
            const response = await fetch(url);
            if (!response.ok) throw new Error('Network response was not ok');
            const result = await response.json();
            // Combine dates and prices
            const formattedStockData = result.map((item)=>({date:new Date(item.timestamp*1000), price:item.price}));
            setStockData(formattedStockData);
        } catch (err) {
            setError(err.message);
        } finally {
            setLoading(false);
        }
    };

    const drawGraph = () => {
        // Set dimensions and margins for the graph
        const margin = { top: 20, right: 30, bottom: 40, left: 40 };
        const width = 800 - margin.left - margin.right;
        const height = 400 - margin.top - margin.bottom;

        // Clear any previous SVG
        d3.select(svgRef.current).selectAll('*').remove();

        // Create an SVG element
        const svg = d3.select(svgRef.current)
            .attr('width', width + margin.left + margin.right)
            .attr('height', height + margin.top + margin.bottom)
            .append('g')
            .attr('transform', `translate(${margin.left},${margin.top})`);

        // Set the scales
        const x = d3.scaleTime()
            .domain(d3.extent(stockData, d => d.date))
            .range([0, width]);

        const y = d3.scaleLinear()
            .domain([0, d3.max(stockData, d => d.price)])
            .range([height, 0]);

        // Add the axes
        svg.append('g')
            .attr('class', 'x-axis')
            .attr('transform', `translate(0,${height})`)
            .call(d3.axisBottom(x));
            // .call(d3.axisBottom(x).tickFormat(d3.timeFormat("%Y-%m-%d")));

        svg.append('g')
            .attr('class', 'y-axis')
            .call(d3.axisLeft(y));

        // Draw the line
        const line = d3.line()
            .x(d => x(d.date))
            .y(d => y(d.price));

        svg.append('path')
            .datum(stockData)
            .attr('fill', 'none')
            .attr('stroke', 'steelblue')
            .attr('stroke-width', 1.5)
            .attr('d', line);
    };

    // Function to calculate profit
    const calculateProfit = () => {
        if (stockData.length === 0) return;

        // Assuming stockData[0].price is the initial price and stockData[stockData.length - 1].price is the final price
        const initialPrice = stockData[0].price;
        const finalPrice = stockData[stockData.length - 1].price;

        // Calculate the profit/loss
        // const sharesBought = investmentAmount / initialPrice;
        // const finalValue = sharesBought * finalPrice;
        // const profitAmount = finalValue - investmentAmount;
        const profitAmount = finalPrice - initialPrice;

        setProfit(profitAmount);
    };

    const fetchEarliestDate = async () => {
        try {
            const response = await axios.get(`http://localhost:5000/api/earliest-date?symbol=${stock}`);
            setEarliestDate(response.data.earliestDate);
            // console.log('earliestDate,startDate',earliestDate,startDate, earliestDate<startDate)
            if(earliestDate < startDate) setStartDate(earliestDate.match(/^(\d{4}-\d{2}-\d{2})/)[1]);
        } catch (error) {
            console.error('Error fetching earliest date:', error);
        }
    };

    useEffect(() => {
        console.log('stock changed',stock);
        fetchStockData();
    }, [debouncedStock, startDate, endDate]);

    useEffect(() => {
        if (stockData.length) {
            drawGraph(); // Call the function to draw the graph
        }
        calculateProfit();
        fetchEarliestDate();
    }, [stockData]);


    const handleSubmit = (e) => {
        e.preventDefault();
        // fetchStockData(); // Fetch data on form submission
    };

    return (
        <div>
            <h1>Stock Investment Graph</h1>
            {earliestDate}
            <form onSubmit={handleSubmit}>
                <table>
                    <thead>
                        <tr>
                            <th>Stock:</th>
                            <th>Start:</th>
                            <th>End:</th>
                            <th colSpan='100%'>Purchase Amount:</th>
                        </tr>
                    </thead>
                    <tbody>
                        <tr>
                            <td>
                                <label>
                                    <input
                                        type="text"
                                        placeholder="Stock Symbol"
                                        value={stock}
                                        onChange={(e) => setStock(e.target.value)}
                                    />
                                </label>
                            </td>
                            <td>
                                <label>
                                    <input
                                        type="date"
                                        min={earliestDate ? new Date(earliestDate).toISOString().split('T')[0] : ''}
                                        value={startDate}
                                        onChange={(e) => setStartDate(e.target.value)}
                                    />
                                </label>
                            </td>
                            <td>
                                <label>
                                    <input
                                        type="date"
                                        value={endDate}
                                        onChange={(e) => setEndDate(e.target.value)}
                                    />
                                </label>
                            </td>
                            <td>
                                <label>
                                    <input
                                        type="number"
                                        value={purchaseAmount}
                                        onChange={(e) => setPurchaseAmount(e.target.value)}
                                        placeholder="Purchase Amount in USD"
                                    />
                                </label>
                            </td>
                            <td>
                                <button type="submit">Fetch Data</button>
                            </td>
                        </tr>
                    </tbody>
                </table>
            </form>
            {loading && <p>Loading...</p>}
            {error && <p>Error: {error}</p>}
            <svg ref={svgRef}></svg> {/* SVG for D3 to draw on */}
            <pre>{formattedStockData}</pre>
        </div>
    );
};

export default StockGraph;
