import React, { useState, useEffect, useRef } from 'react';
import * as d3 from 'd3';
import axios from 'axios';

import useDebounce from './../hooks/useDebounce';

const StockGraph = () => {
    const [stock, setStock] = useState('ANF'); 
    const debouncedStock = useDebounce(stock, 500);
    // const [startDate, setStartDate] = useState('2024-10-15'); // default start date
    const [startDate, setStartDate] = useState('1900-01-01'); // default start date
    const [endDate, setEndDate] = useState('2024-10-16'); // default end date
    const [profit, setProfit] = useState(null);
    const [purchaseAmount, setPurchaseAmount] = useState(100); // default purchase amount $100
    const [stockData, setStockData] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const [earliestDate, setEarliestDate] = useState('');
    const today = new Date().toISOString().split('T')[0]; // YYYY-MM-DD
    const [formattedStockData, setFormattedStockData] = useState('');

    const svgRef = useRef(); // rerender graph everytime svg is changed

    useEffect(() => {
        setFormattedStockData(JSON.stringify(stockData, null, 2)); // 2 spaces for pretty printing
    }, [stockData]);

    useEffect(() => {
        fetchEarliestDate();
    }, [debouncedStock]);

    useEffect(() => {
        if (debouncedStock && startDate && endDate) {
            fetchStockData();
        }
    }, [debouncedStock, startDate, endDate, purchaseAmount]);

    useEffect(() => {
        if (stockData.length) {
            drawGraph();
        }
    }, [stockData]);

    function formatDateString(dateString){
        const match = dateString.match(/^(\d{4}-\d{2}-\d{2})/); // YYYY-MM-DD
        return match ? match[1] : '';
    }

    const fetchStockData = async () => {
        setLoading(true);
        setError(null);
        try {
            const url = `http://localhost:5000/api/stocks?stock=${debouncedStock}&startDate=${startDate}&endDate=${endDate}`;
            const response = await fetch(url);
            if (!response.ok) throw new Error('Network response was not ok');
            const result = await response.json();

            let previousPrice = null; // used whenever price comes back as null
            const formattedStockData = result.map(item => {
                const date = new Date(item.timestamp * 1000);
                const price = item.price !== null ? item.price : previousPrice; // most recent known price
                previousPrice = price;
                return { date, price };
            });
            setStockData(formattedStockData);
            calculateProfit(formattedStockData);
        } catch (err) {
            setError(err.message);
        } finally {
            setLoading(false);
        }
    };

    // returns data point closest to mouse
    const findClosestDataPoint = (stockData, dateAtMouse) => {
        // binary search to find this faster
        var left = 0;
        var right = stockData.length-1;
        while(left < right){
            var mid = Math.floor((right+left)/2);
            var midDate = stockData[mid].date;
            if(midDate.getTime() == dateAtMouse.getTime()) break;
            if(midDate < dateAtMouse){
                left = mid+1;
            }else{
                right = mid-1;
            }
        }
        return stockData[mid];
    }


    const drawGraph = () => {
        // dimensions for graph
        const margin = { top: 20, right: 30, bottom: 40, left: 40 };
        const width = 800 - margin.left - margin.right;
        const height = 400 - margin.top - margin.bottom;

        // remove old graph
        d3.select(svgRef.current).selectAll('*').remove();

        // create new graph
        const svg = d3.select(svgRef.current)
            .attr('width', width + margin.left + margin.right)
            .attr('height', height + margin.top + margin.bottom)
            .append('g')
            .attr('transform', `translate(${margin.left},${margin.top})`);

        let closestData;
        const handleMousemove = (e) => {
            const [mouseX, mouseY] = d3.pointer(e);
            // console.log('mouseX, mouseY',mouseX, mouseY)
            const dateAtMouse = x.invert(mouseX);
            const priceAtMouse = y.invert(mouseY);
            console.log('dateAtMouse,priceAtMouse',dateAtMouse,priceAtMouse)
            closestData = findClosestDataPoint(stockData,dateAtMouse);
            if(closestData){
                trackerLine
                    .attr('stroke','steelblue')
                    .attr('stroke-width',1)
                    .attr("x1", x(dateAtMouse))
                    .attr("x2", x(dateAtMouse))
                    .attr("y1", 0)
                    .attr("y2", height)
                    .style("opacity", 1);

                // Update ball position to match the closest data point
                ball
                    .attr('cx', x(dateAtMouse))
                    .attr('cy', y(closestData.price))
                    .attr('visibility', 'visible');
                if(closestData?.price == undefined) console.log('closestData',closestData)

                // Update tooltip
                tooltip
                    .html(`Date: ${d3.timeFormat("%Y-%m-%d")(closestData.date)}<br>Price: $${closestData?.price?.toFixed(2)}`)
                    .style('pointer-events','none')
                    .style("opacity", 1)
                    .style("left", `${e.pageX + 10}px`)
                    .style("top", `${e.pageY - 20}px`);
            }
        };


        const x = d3.scaleTime()
            .domain(d3.extent(stockData, d => d.date))
            .range([0, width]);

        const y = d3.scaleLinear()
            .domain([0, d3.max(stockData, d => d.price)])
            .range([height, 0]);

        // Add the axes
        svg.append('g')
            .attr('class', 'x-axis')
            .attr('transform', `translate(0,${height})`)
            .call(d3.axisBottom(x));

        svg.append('g')
            .attr('class', 'y-axis')
            .call(d3.axisLeft(y));

        // maps x and y access to date and price attributes
        const line = d3.line()
            .x(d => x(d.date))
            .y(d => y(d.price));

        svg.append('path')
            .attr('class','line')
            .datum(stockData) // plugs in data
            .attr('fill', 'none')
            .attr('stroke', 'steelblue')
            .attr('stroke-width', 1.5)
            .attr('d', line)
            .attr('clip-path','url(#clip)')

        const trackerLine = svg.append('line')
            .attr('stroke','steelblue')
            .attr('stroke-width',1)
            .attr('y1', 0)
            .attr('y2', height)
            .style("opacity", 1);

        // lil ball on the line to show where it intersects the tracker
        const ball = svg.append('circle')
            .attr('r', 3)
            .attr('fill', 'steelblue')
            .attr('stroke', 'steelblue');

        // shows info about garph intersect
        const tooltip = d3.select("body").append("div")
            .attr("class", "tooltip")
            .style("opacity", 0)
            .style("position", "absolute")
            .style("padding", "5px")
            .style("background", "rgba(0, 0, 0, 0.7)")
            .style("color", "white")
            .style("border-radius", "3px");

        // invisible rectangle for detecting mouseover event
        svg.append('rect')
            .attr('width', width)
            .attr('height', height)
            .style('z-index',99)
            .style('fill', 'none')
            .style('pointer-events', 'all');

        svg.append('defs')
            .append('clipPath')
            .attr('id', 'clip')
            .append('rect')
            .attr('width', width)
            .attr('height', height);

        // set up zoom brush
        var brush = d3.brush()
                    // .x(x).y(y)
                    .on('end', (e)=>{
                        if(!e.selection) return
                        console.log(e.selection)
                        var [[x0, y0], [x1, y1]] = e.selection;
                        var minDate = x.invert(x0);
                        var maxDate = x.invert(x1);
                        var minValue = y.invert(y0);
                        var maxValue = y.invert(y1);

                        x.domain([minDate, maxDate]);
                        y.domain([minValue, maxValue]);

                        // Transition and update axes
                        svg.select(".x-axis")
                            .transition()
                            .duration(1000)
                            .call(d3.axisBottom(x));

                        svg.select(".y-axis")
                            .transition()
                            .duration(1000)
                            .call(d3.axisLeft(y));

                        // Re-render any chart elements (like lines, bars, etc.)
                        svg.selectAll('.line') // Assuming you have a line with this class
                            .transition()
                            .duration(1000)
                            .attr('d', line); // Redraw line with updated scales
                    });

        svg.append("g")
            .attr("class", "brush")
            .call(brush);

        svg.on('mousemove', handleMousemove)
        svg.on('mouseout',()=>{
            trackerLine.style('opacity',0);
            tooltip.style('opacity',0);
            ball.style('opacity',0);
        })
        svg.on('click', function(e) {
            console.log('closestData',closestData);
            // console.log('trackerLine',trackerLine)
            var frozenTrackerLine = svg.append('line')
                .style('pointer-events','none')
                .attr('stroke','purple')
                .attr('stroke-width',1)
                .attr('x1', x(closestData.date))
                .attr('x2', x(closestData.date))
                .attr('y1', 0)
                .attr('y2', height)
                .style("opacity", 1);

            // lil ball on the line to show where it intersects the tracker
            var frozenBall = svg.append('circle')
                .style('pointer-events','none')
                .attr('cx', x(closestData.date))
                .attr('cy', y(closestData.price))
                .attr('visibility', 'visible') // Show the ball when data is available
                .attr('r', 3)
                .attr('fill', 'purple')
                .attr('stroke', 'purple');

            // shows info about garph intersect
            var frozenTooltip = d3.select("body").append("div")
                .style('pointer-events','none')
                .html(`Date: ${d3.timeFormat("%Y-%m-%d")(closestData.date)}<br>Price: $${closestData?.price?.toFixed(2)}`)
                .style("left", `${e.pageX + 10}px`)
                .style("top", `${e.pageY - 20}px`)
                .attr("class", "tooltip")
                .style("opacity", 1)
                .style("position", "absolute")
                .style("padding", "5px")
                .style("background", "pink")
                .style("color", "black")
                .style("border-radius", "3px");
        });
    };

    function formatAsCurrency(amount) {
        return new Intl.NumberFormat('en-US', {
            style: 'currency',
            currency: 'USD',
            minimumFractionDigits: 2,
            maximumFractionDigits: 2
        }).format(amount);
    }

    // Function to calculate profit
    const calculateProfit = () => {
        if (stockData.length === 0) return;

        // Assuming stockData[0].price is the initial price and stockData[stockData.length - 1].price is the final price
        const initialPrice = stockData[0].price;
        const shares = (purchaseAmount/initialPrice);
        const finalPrice = stockData[stockData.length - 1].price;
        const profitAmount = ((shares*finalPrice) - (shares*initialPrice)).toFixed(2);
        setProfit(formatAsCurrency(profitAmount));
    };

    const fetchEarliestDate = async () => {
        try {
            const response = await axios.get(`http://localhost:5000/api/earliest-date?symbol=${debouncedStock}`);
            const newEarliestDate = formatDateString(response.data.earliestDate);
            setEarliestDate(newEarliestDate);

            if(new Date(newEarliestDate)>new Date(startDate)) {
                setStartDate(newEarliestDate);
            }

            // Update endDate if it's earlier than the new earliest date
            if (new Date(endDate) < new Date(newEarliestDate)) {
                setEndDate(today); // Reset endDate to today
            }
        } catch (error) {
            console.error('Error fetching earliest date:', error);
        }
    };

    return (
        <div>
            <h1>Stock Investment Graph</h1>
            Earliest date:{earliestDate}
            <br/>
            Start date:{startDate}
            <form>
                <table>
                    <thead>
                        <tr>
                            <th>Stock:</th>
                            <th>Start:</th>
                            <th>End:</th>
                            <th colSpan='100%'>Purchase Amount:</th>
                        </tr>
                    </thead>
                    <tbody>
                        <tr>
                            <td>
                                <label>
                                    <input
                                        type="text"
                                        placeholder="Stock Symbol"
                                        value={stock}
                                        onChange={(e) => setStock(e.target.value)}
                                    />
                                </label>
                            </td>
                            <td>
                                <label>
                                    <input
                                        type="date"
                                        max={today}
                                        min={earliestDate ? new Date(earliestDate).toISOString().split('T')[0] : ''}
                                        value={startDate}
                                        onChange={(e) => {
                                            const selectedDate = e.target.value;
                                            if (new Date(selectedDate) >= new Date(earliestDate) && new Date(selectedDate) <= new Date(today)) {
                                                setStartDate(selectedDate);
                                            }
                                        }}
                                    />
                                </label>
                            </td>
                            <td>
                                <label>
                                    <input
                                        type="date"
                                        max={today}
                                        min={earliestDate ? new Date(earliestDate).toISOString().split('T')[0] : ''}
                                        value={endDate}
                                        onChange={(e) => {
                                            const selectedDate = e.target.value;
                                            if (new Date(selectedDate) >= new Date(earliestDate) && new Date(selectedDate) <= new Date(today)) {
                                                setEndDate(selectedDate);
                                            }
                                        }}
                                    />
                                </label>
                            </td>
                            <td>
                                <label>
                                    <input
                                        type="number"
                                        value={purchaseAmount}
                                        onChange={(e) => setPurchaseAmount(e.target.value)}
                                        placeholder="Purchase Amount in USD"
                                    />
                                </label>
                            </td>
                            <td>
                                {/*<button type="submit">Fetch Data</button>*/}
                            </td>
                        </tr>
                    </tbody>
                </table>
            </form>
            <svg ref={svgRef}></svg> {/* SVG for D3 to draw on */}
            <h2>Profit: {profit}</h2>
            {loading && <p>Loading...</p>}
            {error && <p>Error: {error}</p>}
            <pre>{formattedStockData}</pre>
        </div>
    );
};

